function renderPosition() {
    var objectPosition = {
        front:[
            {name: "อกซ้าย"}
            ,{name: "อกขวา"}
            ,{name: "ใต้สาบ"}
            ,{name: "ด้านหลัง"}
        ],
        back:[
            {name: "อกซ้าย"}
            ,{name: "อกขวา"}
            ,{name: "ใต้สาบ"}
            // ,{name: "ด้านหลัง"}
        ],
        right:[
            {name: "อกซ้าย"}
            ,{name: "อกขวา"}
            // ,{name: "ใต้สาบ"}
            // ,{name: "ด้านหลัง"}
        ]
        ,left:[
            {name: "อกซ้าย"}
            // ,{name: "อกขวา"}
            // ,{name: "ใต้สาบ"}
            // ,{name: "ด้านหลัง"}
        ]
    }
    
    Object.keys(objectPosition).map(function(key, index, parts){
        var side = key;
        var groupPosition = $('<div class="position-group"></div>').attr('side',side).css('display','none');

        side === 'front' ?  groupPosition.css('display','flex') : false;
        
        $.each(objectPosition[side], function (index, item) { 
            var name = item['name'] || false;
            var elePosition = $('<div class="position-item"></div>') || false;
            var inputRadio = $('<input type="radio" name="position"><span>'+name+'</span>') || false;

            // index == 0 ? inputRadio.attr('checked',true) : false;
            index == 0 ? console.log(index): false;

            $(inputRadio).appendTo(elePosition);
            $(elePosition).appendTo(groupPosition);
            $(groupPosition).insertAfter($('.position')); 
        });
       
    });
}

